import React from 'react'
import { useState, useEffect, useRef, useCallback } from 'react'
import { useParams } from 'react-router-dom'
import './Case.css'


function Case(props) {

  const [caseContentsStatus, setCaseContentsStatus] = useState("fetching")
  const [caseContents, setCaseContents] = useState([])
  const [caseContentsImages, setCaseContentsImages] = useState([])
  const [caseContentsImagesLoaded, setCaseContentsImagesLoaded] = useState([])
  const [randomContents, setRandomContents] = useState([])
  // const [rouletteContents, setRouletteContents] = useState([])


  const [play, setPlay] = useState(false)

  const canvasRef = useRef(null)

  const rouletteDuration = useRef(Math.random()*3000+5000)
  const rouletteMove = useRef(0)

  let {id} = useParams()

  useEffect(() => {
    fetch(`http://localhost:3001/contents/${id}`)
      .then(res=>res.json()
        .then(data=>{
          setCaseContentsStatus("fetched")
          setCaseContents(data.contents)
          let contentsImages = []
          let loaded = []
          data.contents.forEach((content, i) => {
            let contentImage = new Image()
            contentImage.src = `http://localhost:3001/image/${content.img}`
            contentImage.addEventListener("load", ()=>{imgLoaded(i)})
            contentsImages.push(contentImage)
            loaded.push(false)
          })
          setCaseContentsImages(contentsImages)
          setCaseContentsImagesLoaded(loaded)
          let randomIndexes = []
          for(let i = 0; i < 6; i++){
            randomIndexes.push(Math.round(Math.random()*(data.contents.length-1)))
          }
          setRandomContents(randomIndexes)
        }))
      .catch(err=>{console.log(err);setCaseContentsStatus("error")})
  }, [id])

  const imgLoaded = (id) => {
    setCaseContentsImagesLoaded(prev => {
      prev[id] = true
      return prev
    })
  }

  const draw = (ctx, frameCount, lfrt, cfrt) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
    let move = rouletteMove.current
    if(play && rouletteDuration.current>0){
      move+=10+Math.round(rouletteDuration.current/1000)
    }
    randomContents.forEach((id, i) => {
      let currentContent = caseContents[id]
      ctx.fillStyle = currentContent.color
      ctx.fillRect(-125+(250*i)-move, 0, 250, 200)
      // console.log(caseContentsImagesLoaded)
      if(caseContentsImagesLoaded[id]){
        ctx.drawImage(caseContentsImages[id], -100+(250*i)-move, 0, 200, 200)
      }
    });
    if(move>250){
      setRandomContents(prev => {
        prev.shift()
        prev.push(Math.round(Math.random()*(caseContents.length-1)))
        return prev
      })
      move-=250
    }
    if(rouletteDuration.current <= 0){
      setPlay(false)
    }
    rouletteMove.current = move
    console.log(cfrt,lfrt)
    rouletteDuration.current -= (cfrt-lfrt)
  }
  useEffect(() => {
    if(caseContentsStatus==="fetched"){
      const canvas = canvasRef.current
      const context = canvas.getContext('2d')
      
      let frameCount = 0
      let animationFrameId
      let lastFrameRenderTime = Date.now()

    
    //Our draw came here
      const render = () => {
        frameCount++
        let currentFrameRenderTime = Date.now()
        draw(context, frameCount, lastFrameRenderTime, currentFrameRenderTime)
        lastFrameRenderTime = currentFrameRenderTime
        animationFrameId = window.requestAnimationFrame(render)
        }
        render()
    
    return () => {
      window.cancelAnimationFrame(animationFrameId)
    }
    }
  }, [draw, caseContentsStatus])

  const click = () => {
    console.log(caseContents)
    console.log(caseContentsStatus)
    setPlay(prev => !prev)
  }

  return (
    <div>
        <div className='navbar'>
            <p onClick={click}>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
        </div>
        <div className='app'>
          <div className='roulette-box'>
            {caseContentsStatus==="fetched"?<div>
              <canvas ref={canvasRef} width="1000" height="200"></canvas>
            </div>:<div>nieok</div>}
          </div>
          {caseContentsStatus==="fetched"?<div className='case-contents'>
            {caseContents.map((el, i) => 
              <div className='case-contents-individual' style={{"backgroundColor": el.color}} key={i}>
                <div style={{"backgroundColor": el.color}}>{el.name}</div><img id={`img${el.name}`} src={`http://localhost:3001/image/${el.img}`} alt={el.name}></img>
              </div>
            )}
          </div>:<div>nieok</div>}
        </div>
    </div>
  )
}

export default Case
