import React from 'react'
import { useState, useEffect, useRef } from 'react'
import { useParams } from 'react-router-dom'
import './Case.css'


function Case(props) {

  const [caseContentsStatus, setCaseContentsStatus] = useState("fetching")
  const [caseContents, setCaseContents] = useState([])
  // const [rouletteContents, setRouletteContents] = useState([])

  const canvasRef = useRef(null)


  let {id} = useParams()

  useEffect(() => {
    fetch(`http://localhost:3001/contents/${id}`)
      .then(res=>res.json()
        .then(data=>{
          setCaseContentsStatus("fetched")
          setCaseContents(data.contents)
          let rouletteContentsHelper = []
          for(let i = 0; i<256; i++){
            rouletteContentsHelper.push(data.contents[Math.round(Math.random()*(data.contents.length-1))])
          }
          console.log(rouletteContentsHelper)
          setRouletteContents(rouletteContentsHelper)
        }))
      .catch(err=>{setCaseContentsStatus("error")})
  }, [id])

  useEffect(() => {
    const canvas = canvasRef.current
    const context = canvas.getContext('2d')
    //Our first draw
    context.fillStyle = '#000000'
    context.fillRect(0, 0, context.canvas.width, context.canvas.height)
  }, [])

  const click = () => {
    console.log(caseContents)
  }

  return (
    <div>
        <div className='navbar'>
            <p onClick={click}>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
        </div>
        <div className='app'>
          <div className='roulette-box'>
            {caseContentsStatus==="fetched"?<div>
              <canvas ref={canvasRef}></canvas>
            </div>:<div>nieok</div>}
          </div>
          {caseContentsStatus==="fetched"?<div className='case-contents'>
            {caseContents.map((el, i) => 
              <div className='case-contents-individual' style={{"backgroundColor": el.color}} key={i}>
                <div style={{"backgroundColor": el.color}}>{el.name}</div><img src={`http://localhost:3001/image/${el.img}`} alt={el.name}></img>
              </div>
            )}
          </div>:<div>nieok</div>}
        </div>
    </div>
  )
}

export default Case
