import React from 'react'
import { useState, useEffect, useRef } from 'react'
import { useParams } from 'react-router-dom'
import './Case.css'


function Case(props) {

  const [caseContentsStatus, setCaseContentsStatus] = useState("fetching")
  const [caseContents, setCaseContents] = useState([])
  const [caseContentsImages, setCaseContentsImages] = useState([])
  const [randomContents, setRandomContents] = useState([])
  // const [rouletteContents, setRouletteContents] = useState([])

  const canvasRef = useRef(null)


  let {id} = useParams()

  useEffect(() => {
    fetch(`http://localhost:3001/contents/${id}`)
      .then(res=>res.json()
        .then(data=>{
          setCaseContentsStatus("fetched")
          setCaseContents(data.contents)
          let contentsImages = []
          data.contents.forEach(content => {
            let contentImage = document.createElement("img")
            contentImage.src = content.img
            contentsImages.push(contentImage)
          })
          setCaseContentsImages(contentsImages)
          let randomIndexes = []
          for(let i = 0; i < 6; i++){
            randomIndexes.push(Math.round(Math.random()*(data.contents.length-1)))
          }
          setRandomContents(randomIndexes)
        }))
      .catch(err=>{console.log(err);setCaseContentsStatus("error")})
  }, [id])

  

  const draw = (ctx, frameCount, lfrt) => {
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height)
    randomContents.forEach((id, i) => {
      let currentContent = caseContents[id]
      console.log(currentContent)
      ctx.fillStyle = currentContent.color
      ctx.fillRect(-125+(250*i), 0, 250, 200)
      ctx.drawImage(caseContentsImages[id], -100+(250*i), 0, 200, 200)
    });
  }
  useEffect(() => {
    

    if(caseContentsStatus==="fetched"){
      const canvas = canvasRef.current
      const context = canvas.getContext('2d')
      
      let frameCount = 0
      let animationFrameId
      let lastFrameRenderTime = Date.now()

    
    //Our draw came here
      const render = () => {
        frameCount++
        draw(context, frameCount, lastFrameRenderTime)
        lastFrameRenderTime = Date.now()
        animationFrameId = window.requestAnimationFrame(render)
        }
        render()
    
    return () => {
      window.cancelAnimationFrame(animationFrameId)
    }
    }
  }, [draw, caseContentsStatus])

  const click = () => {
    console.log(caseContents)
    console.log(caseContentsStatus)
  }

  return (
    <div>
        <div className='navbar'>
            <p onClick={click}>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
            <p>DUPA</p>
        </div>
        <div className='app'>
          <div className='roulette-box'>
            {caseContentsStatus==="fetched"?<div>
              <canvas ref={canvasRef} width="1000" height="200"></canvas>
            </div>:<div>nieok</div>}
          </div>
          {caseContentsStatus==="fetched"?<div className='case-contents'>
            {caseContents.map((el, i) => 
              <div className='case-contents-individual' style={{"backgroundColor": el.color}} key={i}>
                <div style={{"backgroundColor": el.color}}>{el.name}</div><img id={`img${el.name}`} src={`http://localhost:3001/image/${el.img}`} alt={el.name}></img>
              </div>
            )}
          </div>:<div>nieok</div>}
        </div>
    </div>
  )
}

export default Case
